import math

# --- Helper Functions ---

def round_number(num):
    if num == 0:
        return 0
    if abs(num) >= 0.001:
        return round(num, 3)
    else:
        # Basic rounding for very small numbers, adjust if specific significant digit logic is needed
        return round(num, -int(math.floor(math.log10(abs(num)))) + 2)

def age_conversion(input_age):
    # Directly return age in years, assuming input is [value, 'years']
    if len(input_age) == 2 and input_age[1] == 'years':
        return input_age[0]
    elif len(input_age) == 2 and input_age[1] == 'months':
        # Convert months to years, rounding down or handling appropriately
        return input_age[0] // 12
    # Add other units if necessary
    return None # Indicate error or handle other cases

def height_conversion_m(height_info):
    # Converts height to meters
    value = height_info[0]
    unit = height_info[1]
    if len(height_info) == 4: # e.g., [5, 'ft', 10, 'in']
        inches = height_info[0] * 12 + height_info[2]
        meters = round_number(inches * 0.0254)
        return meters
    elif unit == "m":
        return value
    elif unit == "cm":
        return round_number(value / 100)
    elif unit == "ft":
         return round_number(value * 0.3048)
    elif unit == "in":
         return round_number(value * 0.0254)
    return None

def height_conversion_in(height_info):
     # Converts height to inches
    value = height_info[0]
    unit = height_info[1]
    if len(height_info) == 4: # e.g., [5, 'ft', 10, 'in']
        feet = height_info[0]
        inches_part = height_info[2]
        total_inches = round_number(feet * 12 + inches_part)
        return total_inches
    elif unit == "m":
        return round_number(value * 39.3701)
    elif unit == "cm":
        return round_number(value * 0.393701)
    elif unit == "ft":
        return round_number(value * 12)
    elif unit == "in":
        return value
    return None

def weight_conversion_kg(weight_info):
    # Converts weight to kg
    weight = weight_info[0]
    unit = weight_info[1]
    if unit == "lbs":
        return round_number(weight * 0.453592)
    elif unit == "g":
        return round_number(weight / 1000)
    elif unit == "kg":
        return weight
    return None

def calculate_bmi(weight_kg, height_m):
    if height_m is None or height_m == 0:
        return 0
    return round_number(weight_kg / (height_m * height_m))

def calculate_ibw(height_in, sex):
    if height_in is None:
        return 0
    # Devine formula requires height >= 60 inches
    height_diff = height_in - 60
    if height_diff < 0:
        height_diff = 0  # Use 0 if height is less than 60 inches

    if sex == "Male":
        # IBW = 50 kg + 2.3 kg for each inch over 5 feet
        ibw = round_number(50 + 2.3 * height_diff)
    elif sex == "Female":
         # IBW = 45.5 kg + 2.3 kg for each inch over 5 feet
        ibw = round_number(45.5 + 2.3 * height_diff)
    else:
        ibw = 0 # Or handle error
    return max(ibw, 0)

# --- Input Parameters ---
params = {
    "sex": "Female",
    "weight": (55.0, "kg"),
    "height": (162.8, "cm"),
    "creatinine": (0.57, "mg/dL"),
    "age": (16, "years")
}

# --- Extract and Convert Parameters ---
age = age_conversion(params["age"])
sex = params["sex"]
actual_weight_kg = weight_conversion_kg(params["weight"])
height_m = height_conversion_m(params["height"])
height_in = height_conversion_in(params["height"])

# Ensure creatinine value exists and handle potential errors
if "creatinine" in params and len(params["creatinine"]) >= 1:
    serum_creatinine_mg_dl = params["creatinine"][0] # Assumes already in mg/dL
else:
    serum_creatinine_mg_dl = 0 # Default or error handling

# --- Calculate Intermediate Values ---
bmi = calculate_bmi(actual_weight_kg, height_m)
ibw_kg = calculate_ibw(height_in, sex)

# --- Determine Weight to Use in Cockcroft-Gault ---
weight_to_use = 0
if bmi == 0: # Handle case where BMI couldn't be calculated
    weight_to_use = actual_weight_kg # Default to actual weight or handle error
elif bmi < 18.5:
    weight_to_use = actual_weight_kg
elif 18.5 <= bmi <= 24.9:
    # Use minimum of ideal body weight and actual weight for normal BMI
    weight_to_use = min(ibw_kg, actual_weight_kg)
else: # overweight/obese (BMI > 24.9)
    # Calculate Adjusted Body Weight (ABW)
    abw_kg = round_number(ibw_kg + 0.4 * (actual_weight_kg - ibw_kg))
    weight_to_use = abw_kg

# --- Determine Gender Coefficient ---
gender_coefficient = 1.0 if sex == "Male" else 0.85

# --- Calculate Creatinine Clearance ---
# Cockcroft-Gault Formula: CrCl = ((140 - age) * weight_to_use * gender_coefficient) / (serum_creatinine * 72)
# Ensure no division by zero and age/weight are valid
if serum_creatinine_mg_dl <= 0 or age is None or weight_to_use <= 0:
     creatinine_clearance_ml_min = 0 # Assign 0 or handle error appropriately
else:
    numerator = (140 - age) * weight_to_use * gender_coefficient
    denominator = serum_creatinine_mg_dl * 72
    creatinine_clearance_ml_min = round_number(numerator / denominator)

# --- Store final result and print ---
result = creatinine_clearance_ml_min
print(result)
